# @return {Integer[]}
def two_sum(nums, target)
  h = {}
  nums.each_with_index { |n,i| h[n] = i } 

  nums.each_with_index do |n, i|
    return [i, h[target-n]] if h.has_key?(target-n) && i != h[target-n]
  end
end

# @param {String} s
# @return {Integer}
def length_of_longest_substring(s)
    seen_ = Hash.new;
    size = s.size
    lp = 0; rp = 0;
    max_len = 0

    while(rp < size)
      idx = seen_[s[rp]];
      # lp<=idx means char is present and within current substring, so update left pointer
      # else, repeated is outside of current lp - rp substring, so just update max_len
      if !idx.nil? && lp <= idx
        lp = idx+1
      else
        max_len = [max_len, rp-lp+1].max
      end
      seen_[s[rp]] = rp;
      rp+=1
    end
    max_len
end

