def is_valid_sudoku(board)
  h_row = Array.new(9) {Set.new}; h_col = Array.new(9) {Set.new}; 
  h_box = Hash.new {|h,k| h[k]=Set.new}

  (0...9).each do |i|
    (0...9).each do |j|
      value = board[i][j]
      next if value == "."; box_idx = (i/3).to_s + (j/3).to_s;
      return false if h_row[i].include?(value) || h_col[j].include?(value) || h_box[box_idx].include?(value)
      h_row[i] << value; h_col[j] << value; h_box[box_idx] << value
    end
  end
  true
end

# @param {Integer[]} height
# @return {Integer}
def trap(height)
  n = height.size
  calc =-> (a,b) { [a,b].max }
  left_max_till_i = [height[0]]
  right_max_till_i = [height[-1]]
  (1...n).each { |j| left_max_till_i << calc.call(left_max_till_i.last, height[j]) }
  (n-2).downto(0).each { |j| right_max_till_i.unshift( calc.call(right_max_till_i.first, height[j]) )}

  (0...n).reduce(0) do |res,i|
    # min of (left max till now, right max till now) - current height; ith index will store this volume.
    volume_at_i = [left_max_till_i[i], right_max_till_i[i]].min - height[i]
    res + volume_at_i
  end
end

