def unique_paths(m, n)
  factorial=->(i) { i==0 ? 1 : (1..i).reduce(&:*) }
  factorial.call( m-1+n-1) / ( factorial.call(m-1) * factorial.call(n-1) )
end

# @param {Integer[][]} grid
# @return {Integer}
def min_path_sum(grid)
  m = grid.size
  n = grid[0].size
  dp = Array.new(m) { Array.new(n, -1) }
  dp[0][0] = grid[0][0]

  (1...n).each { |j| dp[0][j] = dp[0][j-1] + grid[0][j] }
  (1...m).each { |j| dp[j][0] = dp[j-1][0] + grid[j][0] }

  (1...m).each do |i|
    (1...n).each do |j|
      dp[i][j] = grid[i][j] + [dp[i-1][j], dp[i][j-1]].min
    end
  end

  dp[m-1][n-1]
end

