 *     int val;
 *     ListNode *next;
 *     ListNode(int x) : val(x), next(NULL) {}
 * };
 */
class Solution {
public:
    
ListNode* deleteDuplicates(ListNode* head) {
     if(head==NULL || head->next==NULL) return head;
     ListNode* first=head;
     ListNode* second=head->next;

     while(second!=NULL){

     	if(first->val == second->val){
     		second=second->next;
     	}
     	else{
     		first->next=second;
     		first=second;
     		second=first->next;
     	}
     }
     first->next=NULL;
     return head;
    }

};

