/**
 * Definition for singly-linked list.
 * struct ListNode {
 *     int val;
 *     ListNode *next;
 *     ListNode(int x) : val(x), next(NULL) {}
 * };
 */
class Solution {
public:
    ListNode* reverseBetween(ListNode* A, int m, int n) {
        if(m==n)
        return A;
    ListNode *previous,*current,*front,*start=NULL,*end=NULL;
    previous=NULL;
    current=A;
    front=A->next;
    int i=1;
    while(front && i<n){
        if(i==m){
            start=current;
        }
        else if(i==m-1)
            end=current;
        previous=current;
        current=front;
        front=front->next;
        if(i>=m)
            current->next=previous;
        i++;
    }
    if(start)
        start->next=front;
    if(end)
        end->next=current;
    else A=current;
    return A;
    }
};

/**
 * Definition for a binary tree node.
 * struct TreeNode {
 *     int val;
 *     TreeNode *left;
 *     TreeNode *right;
 *     TreeNode(int x) : val(x), left(NULL), right(NULL) {}
 * };
 */
class Solution {
public:
    vector<int> inorderTraversal(TreeNode *root) {
        vector<int> vector;
        if(!root)
        return vector;
        stack<TreeNode *> stack;
        stack.push(root);
        while(!stack.empty())
        {
            TreeNode *pNode = stack.top();
            if(pNode->left)
            {
                stack.push(pNode->left);
                pNode->left = NULL;
            }
            else
            {
                vector.push_back(pNode->val);
                stack.pop();
                if(pNode->right)
                stack.push(pNode->right);
            }
        }
        return vector;
    }
};

# @param {String} s1
# @param {String} s2
# @param {String} s3
# @return {Boolean}
def is_interleave(s1, s2, s3)
  # take s1 first, or s2 first alternatively, and apply on remaining strings
  @s1 = s1; @s2 = s2; @s3= s3;
  return false if s1.size + s2.size != s3.size
  @cache = {}

  recur(0, 0, 0)
end

# call further only if it is matching
def recur(s1_idx, s2_idx, s3_idx)
  return true if s3_idx == @s3.size && s1_idx == @s1.size && s2_idx == @s2.size
  return false if s3_idx == @s3.size

  return @cache[[s1_idx, s2_idx, s3_idx]] if !@cache[[s1_idx, s2_idx, s3_idx]].nil?

  take_s1 = false;
  if @s1[s1_idx] == @s3[s3_idx]
    @cache[[s1_idx, s2_idx, s3_idx]] = recur(s1_idx+1, s2_idx, s3_idx+1)
    take_s1 = @cache[[s1_idx, s2_idx, s3_idx]]
  end

  take_s2 = false
  if @s2[s2_idx] == @s3[s3_idx]
    @cache[[s1_idx, s2_idx, s3_idx]] = recur(s1_idx, s2_idx+1, s3_idx+1)
    take_s2 = @cache[[s1_idx, s2_idx, s3_idx]]
  end

  take_s1 || take_s2
end

/**
 * Definition for a binary tree node.
 * struct TreeNode {
 *     int val;
 *     TreeNode *left;
 *     TreeNode *right;
 *     TreeNode(int x) : val(x), left(NULL), right(NULL) {}
 * };
 */
class Solution {
public:
    int symetric(TreeNode *A, TreeNode *B){

    if(!A && B)
        return 0;
    if(!B && A)
        return 0;
    if(!A)
        return 1;
    if(A->val != B->val)
        return 0;
    return (symetric(A->left,B->right) && symetric(A->right,B->left));
    
}
    bool isSymmetric(TreeNode* A) {
        return symetric(A,A);
    }
};

/**
 * Definition for a binary tree node.
 * struct TreeNode {
 *     int val;
 *     TreeNode *left;
 *     TreeNode *right;
 *     TreeNode(int x) : val(x), left(NULL), right(NULL) {}
 * };
 */
class Solution {
public:
    
    bool hasPathSum(TreeNode* root, int sum) {
        if (root==NULL) return false;
        
        if (root->left==NULL && root->right==NULL ){
            return (root->val==sum);
        }
        
        if (root->left){
            root->left->val += root->val;
            if (hasPathSum(root->left, sum)){
                return true;
            }
        }
        
        if (root->right){
            root->right->val += root->val;
            if (hasPathSum(root->right, sum)){
                return true;
            }
        }
        
        return false;
    }
};

