def max_profit(prices)
  prices.reduce([prices.first, 0 , Float::INFINITY, 0]) {|(t1_cost, t1_profit, t2_cost, t2_profit) , price|
    [
        [t1_cost, price].min,
        [t1_profit, price - t1_cost].max,
        [t2_cost, price - t1_profit].min,
        [t2_profit, price - t2_cost].max
    ]
  }.last
end

