def can_finish(num_courses, prerequisites)
  adj = Hash.new { |h, k| h[k] = [] }
  prerequisites.each { |(a, b)| adj[b] << a }

  visited = Set.new
  visiting = []
  0.upto(num_courses - 1) { |i| return false if find_cycle(i, adj, visited, visiting) }
  true
end

def find_cycle(i, adj, visited, visiting)
  return true if visiting.include?(i)
  return false if visited.include?(i)

  visited << i
  visiting.push(i)
  adj[i].each { |j| return true if find_cycle(j, adj, visited, visiting) }
  visiting.pop

  false
end

