def rob_simple(nums)
  n = nums.size; return nums.max if n < 3

  previous_robbed = nums[0]; previous_skipped = 0;
  current_robbed = nil; current_skipped = nil;
  (1...n).each do |i|
    current_robbed = previous_skipped + nums[i];
    current_skipped = [ previous_robbed, previous_skipped ].max

    previous_skipped = current_skipped
    previous_robbed = current_robbed
  end;

  [current_robbed, current_skipped].max
end

