# Read-Only for queries of sum of [l,r]
# pseudo_tree.query(l, r+1)

class PseudoSumSegTree
  def initialize(arr)
    @n = arr.size # size of the source array
    # query can have r till n, so sum of all elements is at prefix[n]
    @prefix_sum_arr = (1..@n).reduce([0]) {|res, i| res << res.last + arr[i-1]; res } # sum till i-1
  end

  # [l,r)
  def query(l,r)
    @prefix_sum_arr[r] - @prefix_sum_arr[l]
  end
end

def find_target_sum_ways(nums, target)
  n = nums.size;
  psg = PseudoSumSegTree.new(nums)
  cache = {}
  recurse =->(idx, sum) {
    return (sum==target ? 1 : 0) if idx==n
    return 0 if (target-sum).abs > psg.query(idx, n)
    cache[[idx, sum]] ||= (recurse.call(idx+1, sum+nums[idx]) + recurse.call(idx+1, sum-nums[idx]))
  }
  recurse.call(0, 0)
end

def recurse(amount, index)
  return 1 if amount == 0
  return 0 if amount < 0
  return 0 if index > @coins.size - 1

#   return @mem[amount][index] if @mem[amount][index]

  @mem[amount][index] ||= recurse(amount - @coins[index], index) + recurse(amount, index + 1)
end

def change(amount, coins)
  # amount cannot be made from coins that bigger that amount. so just filter all those coins out
  coins.select! { |coin| coin <= amount }
  @coins = coins.sort {|x,y| -(x <=> y)}
  @mem = Array.new(amount + 1) { Array.new(coins.size) }
  recurse(amount, 0)
end

