class Solution:
    def numBusesToDestination(self, routes: List[List[int]], source: int, target: int) -> int:
        if source == target:
            return 0

        # Convert routes to sets for faster lookup
        route_nodes = [set(route) for route in routes]

        # Find all routes that contain the source
        source_nodes = [idx for idx, route in enumerate(route_nodes) if source in route]
        if not source_nodes:
            return -1

        # Build adjacency list of routes that share at least one stop
        adj_h = defaultdict(list)
        r = len(route_nodes)

        for i in range(r):
            for j in range(i + 1, r):
                # If routes i and j have intersection (shared stops)
                if route_nodes[i] & route_nodes[j]:
                    adj_h[i].append(j)
                    adj_h[j].append(i)

        # BFS to find minimum number of buses
        queue = deque(source_nodes)
        dist = 1
        visited = set()

        while queue:
            # Process all nodes at current distance level
            queue_nodes = []
            for _ in range(len(queue)):
                node = queue.popleft()
                queue_nodes.append(node)
                visited.add(node)

            for node_i in queue_nodes:
                # Check if current route contains target
                if target in route_nodes[node_i]:
                    return dist

                # Add adjacent routes to queue
                for adjacent_route in adj_h[node_i]:
                    if adjacent_route not in visited:
                        queue.append(adjacent_route)

            dist += 1

        return -1

# @param {Integer} n
# @param {Integer[][]} edges
# @return {Integer[]}
def sum_of_distances_in_tree(n, edges)
  graph = Hash.new {|h,k| h[k]=[]}
  edges.each {|u,v| graph[u]<<v; graph[v]<<u}

  sbtree_node_count = Array.new(n,0)
  sbtree_i_dist_sum = Array.new(n,0) # sum of dist to all nodes for isolated subtree with i as root
  answer = Array.new(n,0) # this is our result

  pre_dfs_nc =->(node=0, parent=nil) { # calculate node counts for sb-trees
    sbtree_node_count[node] = 1 + graph[node].select{|x| x!=parent}.sum {|x| pre_dfs_nc.call(x, node) }
  }

  pre_dfs_ds =->(node=0, parent=nil) { # calculate dist sum all for isolated sb-trees
    sbtree_i_dist_sum[node] = graph[node].select{|x| x!=parent}.sum { |x| pre_dfs_ds.call(x, node) + sbtree_node_count[x] }
  }

  # 0 will be our root
  pre_dfs_nc.call
  pre_dfs_ds.call
  answer[0] = sbtree_i_dist_sum[0] # for root, i_dist_sum = answer

  # propagate the result from root to children nodes, using the recurrence relation
  dp_dfs =->(node=0, parent=nil) {
    graph[node].select{|x| x!=parent}.each do |child|
      answer[child] = answer[node] + n - 2*sbtree_node_count[child] 
      dp_dfs.call(child, node)
    end
  }

  dp_dfs.call
  answer
end

