# @param {Integer} n
# @return {Integer}
def transition_matrix(m)
  next_colors = {0=>[1,2], 1=>[0,2], 2=>[0,1]}
  # Generate all valid row configurations, start with a color, and build out the state
  states = []
  generate =->(order) {
    return states << order.dup if order.size == m
    next_colors[order[-1]].each {|nc| generate.call(order + [nc]) }
  }
  next_colors.keys.each { |x| generate[[x]] }

  compatible=->(a,b) { (0...m).all? { |i| a[i]!=b[i] } }

  s = states.size
  transition_matrix = Array.new(s) { Array.new(s, 0) }
  (0...s).each { |i| ((i+1)...s).each { |j| 
    (transition_matrix[i][j]=1; transition_matrix[j][i]=1) if (compatible[states[i],states[j]]) 
  } }
  transition_matrix
end

@t_matrix = transition_matrix(3)

def num_of_ways(n)
  big_mod = 10**9 + 7
  s = @t_matrix.size

  matrix_pow = ->(mat, pow) {
    result = Array.new(s) { |i| Array.new(s) { |j| i == j ? 1 : 0 } }
    base = mat.map(&:dup)

    while pow > 0
      if pow.odd?
        result = (0...s).map do |i|
          (0...s).map { |j| (0...s).sum { |k| result[i][k] * base[k][j] } % big_mod }
        end
      end
      base = (0...s).map do |i|
        (0...s).map { |j| (0...s).sum { |k| base[i][k] * base[k][j] } % big_mod }
      end
      pow >>= 1
    end
    result
  }

  matrix_pow.call(@t_matrix, n-1).flatten.sum % big_mod
end

# @param {Integer} num_courses
# @param {Integer[][]} prerequisites
# @param {Integer[][]} queries
# @return {Boolean[]}
def check_if_prerequisite(num_courses, prerequisites, queries)
  # If we reverse the pre-req graph, we have to find for query u,v, is there a path from v to u or not
  # Since n is small, we can calculate all reachable(pre-reqs) nodes for each node and keep it in a set
  rg = Hash.new {|h,k| h[k]=[]}
  rg_ideg = Array.new(num_courses, 0)
  prerequisites.each {|x,y| rg[y]<<x; rg_ideg[x]+=1}
  all_pre_reqs = Array.new(num_courses) # also used as visited

  dfs=->(node) {
    return all_pre_reqs[node] unless all_pre_reqs[node].nil?
    reachable = Set.new
    rg[node].each { |next_node| reachable = reachable | [next_node] | dfs.call(next_node) }
    all_pre_reqs[node] = reachable # update and return reachable set from this node
  }

  # start dfs from all ideg==0 nodes, and record reachable set using dfs
  (0...num_courses).select {|i| rg_ideg[i]==0 }.each(&dfs)
  queries.map {|u,v| all_pre_reqs[v].include?(u) }
end

