# @param {String[]} words
# @param {String} target
# @return {Integer}
def num_ways(words, target)
  target_len = target.size; target = target.chars;
  word_len = words[0].size;
  words = words.map(&:chars)
  freq_arr = Array.new(word_len) { Hash.new(0) }
  words.each { |word| word.each_with_index { |ch, idx| freq_arr[idx][ch]+=1} }
  big_mod = 1_000_000_000 + 7
  cache = Array.new(word_len+1) { Array.new(target_len+1) } 

  recursive =->(w_i, t_i) {
    return 1 if t_i == target_len
    return (t_i == target_len ? 1 : 0) if w_i == word_len

    # take or skip
    cache[w_i][t_i] ||= (freq_arr[w_i][target[t_i]] * recursive.call(w_i+1, t_i+1) + recursive.call(w_i+1, t_i))
  }

  recursive.call(0,0) % big_mod
end

