def restore_array(adjacent_pairs)
  # middle elements would appear twice in pairs arr
  # start/end will appear once, we can choose any as start or end.
  neighbors = Hash.new{|h,k|h[k]=[]}
  adjacent_pairs.each { |i,j| neighbors[i]<<j; neighbors[j]<<i; }
  start = neighbors.keys.detect{|k| neighbors[k].size==1 }
  nums = [start]

  adjacent_pairs.size.times do
   next_num = neighbors[nums[-1]].detect { |x| x!=nums[-2]}
   nums << next_num;
  end
  nums
end

