class Bank
  attr_reader :n
  def initialize(balance)
    @balances = balance
    @n = balance.size
  end

  def has_money?(account, money)
    @balances[account-1]>=money
  end

  def transfer(account1, account2, money)
    return false unless account1.between?(1,n) && account2.between?(1,n)
    return false unless has_money?(account1, money)
    @balances[account1-1]-=money; @balances[account2-1]+=money # txn
    true
  end

  def deposit(account, money)
    return false unless account.between?(1,n)
    @balances[account-1]+=money
    true
  end

  def withdraw(account, money)
    return false unless account.between?(1,n)
    return false unless has_money?(account, money)
    @balances[account-1]-=money
    true
  end
end

