# @param {Integer[]} parents
# @return {Integer}
def count_highest_score_nodes(parents)
  # sb_tree_size, score is l_child * r_child * (n-sb_size)
  # calculate sub-tree size of all
  n = parents.size
  children = Hash.new {|h,k| h[k]=[]}
  parents.each_with_index {|parent, node| children[parent]<<node if parent!=-1}
  sb_tree_size = Array.new(n, 0)

  dfs=->(node){ sb_tree_size[node] = 1 + children[node].sum(&dfs)}
  dfs.call(0)

  scores = Array.new(n, 0)
  (0...n).each do |node|
    sb_sizes = children[node].map {|x| sb_tree_size[x]} + [n - sb_tree_size[node]]
    scores[node] = sb_sizes.select{|x| x>0}.reduce(&:*)
  end

  max_val = scores.max
  scores.count {|x| x==max_val}
end

# @param {Integer[][]} pairs
# @return {Integer[][]}
def valid_arrangement(pairs)
  n = pairs.size
  adj_h = Hash.new {|h,k| h[k]=[]}
  in_degree = Hash.new(0); out_degree = Hash.new(0);
  pairs.each { |u,v| adj_h[u]<<v; in_degree[v]+=1; out_degree[u]+=1; }
  start_node = out_degree.keys.detect {|node| out_degree[node] == in_degree[node]+1 } || pairs[0][0]
  nodes = []

  dfs=->(node){
    dfs.call(adj_h[node].pop) until adj_h[node].empty?
    nodes << node
  }

  dfs.call(start_node)
  nodes.reverse!

  (1..n).map { |i| [nodes[i-1], nodes[i]] }
end

