class Solution:
    def minimumWeight(self, n: int, edges: List[List[int]], src1: int, src2: int, dest: int) -> int:
        adj = defaultdict(list)
        rev_adj = defaultdict(list)
        for u, v, w in edges:
            adj[u].append((v, w))
            rev_adj[v].append((u, w))

        def dijkstra(graph, source):
            pq = [(0, source)]
            dist = [float('inf')] * n

            while pq:
                d, node = heappop(pq)
                if d >= dist[node]:
                    continue
                dist[node] = d
                for neighbor, w in graph[node]:
                    if dist[neighbor] == float('inf'):
                        heappush(pq, (d + w, neighbor))
            return dist

        src1_dist = dijkstra(adj, src1)
        src2_dist = dijkstra(adj, src2)
        dist_from_d = dijkstra(rev_adj, dest)

        res = min((src1_dist[i] + src2_dist[i] + dist_from_d[i] for i in range(n)))
        return -1 if res == float('inf') else res

# @param {Integer} m
# @param {Integer} n
# @param {Integer[][]} guards
# @param {Integer[][]} walls
# @return {Integer}
def count_unguarded(m, n, guards, walls)
  grid = Array.new(m) { Array.new(n, 1) }
  walls.each {|x,y| grid[x][y] = 0 }
  guards.each {|x,y| grid[x][y] = -1}
  directions = [[0,1],[0,-1],[1,0],[-1,0]]
  # 1 is unguarded, -1 is a guard, 0 is a wall, -2 is guarded cell

  travel=->(i,j,dir_idx) {
    di,dj = directions[dir_idx]
    n_i = i+di; n_j= j+dj
    if n_i.between?(0,m-1) && n_j.between?(0,n-1) && grid[n_i][n_j]!=0 && grid[n_i][n_j]!=-1
      grid[n_i][n_j]=-2; travel.call(n_i,n_j,dir_idx)
    end
  }

  # for each guard and direction mark guarded cells
  guards.each { |g| (0..3).each {|dir_idx| travel.call(g[0],g[1], dir_idx)} }
  grid.map {|row| row.count{|x| x>0} }.sum
end

