def product_queries(n, queries)
  big_mod = 1_000_000_000 + 7
  powers = []
  n.to_s(2).reverse.chars.each_with_index {|x,i| powers << 2**i if x=='1' }
  pow_prefix = (0...powers.size).reduce([1]) {|arr, i| arr << arr.last*powers[i]; arr }
  queries.map {|l,r| (pow_prefix[r+1]/pow_prefix[l]) % big_mod }
end

