def min_edge_reversals(n, edges)
  dg = Hash.new {|h,k| h[k]=Set.new} # directed graph
  udg = Hash.new {|h,k| h[k]=Set.new} # undirected graph
  edges.each {|u,v| dg[u]<<v; udg[u]<<v; udg[v]<<u; }
  scores= Array.new(n)

  calculate_score=->(node, parent, score) {
    udg[node].select {|x| x!=parent}.sum do |neighbor|
      calculate_score.call(neighbor, node, score) + (dg[node].include?(neighbor) ? 0 : 1)
    end
  }

  update_scores=->(node, parent, score) {
    scores[node] = score
    udg[node].select {|x| x!=parent}.each { |neighbor| update_scores.call(neighbor, node, score + (dg[node].include?(neighbor) ? 1 : -1) ) }
  }

  update_scores.call(0, -1, calculate_score.call(0,-1,0))
  scores
end

