def dfs(vertex, parent, t)
  return 0 if t > 14 # after 14 halving value will reach 0
  return @cache[vertex][t] unless @cache[vertex][t].nil?

  current_value = @coins[vertex] >> t

  option1 = current_value - @k
  option2 = (current_value >> 1)

  valid_children = @adj[vertex].select{|x| x!=parent }
  valid_children.each do |v|
    option1 += dfs(v,vertex,t)
    option2 += dfs(v,vertex,t+1)
  end;

  @cache[vertex][t] = [option1, option2].max
end

class Solution:
    def leftmostBuildingQueries(self, heights: List[int], queries: List[List[int]]) -> List[int]:
        n = len(heights)
        q = len(queries)

        # Convert queries to list of tuples with original indices and sort by r value descending
        queries_with_index = [(tuple(sorted(query)), i) for i, query in enumerate(queries)]
        queries_with_index.sort(key=lambda x: -x[0][1])  # Sort by r value in descending order

        answer = [-1] * q
        stack = []
        mono_ptr = n - 1

        for (l, r), q_i in queries_with_index:
            if l == r or heights[r] > heights[l]:
                answer[q_i] = r
            else:
                while mono_ptr >= r:
                    # Maintain monotonic decreasing stack
                    while stack and heights[stack[-1]] <= heights[mono_ptr]:
                        stack.pop()
                    stack.append(mono_ptr)
                    mono_ptr -= 1

                # Binary search for first element greater than heights[l]
                left, right = 0, len(stack)-1
                target = heights[l]
                idx = -1

                while left <= right:
                    mid = (left + right) // 2
                    if heights[stack[mid]] > target:
                        idx = stack[mid]
                        left = mid+1
                    else:
                        right = mid - 1

                answer[q_i] = idx

        return answer

class Solution:
    def rabin_karp(self, haystack, needle):
        base = 31
        mod = 1_000_000_000 + 7
        n = len(needle)

        # Calculate needle hash
        needle_hash = 0
        for i, ch in enumerate(reversed(needle)):
            needle_hash = (needle_hash + (ord(ch) - ord('a')) * pow(base, i, mod)) % mod

        # Calculate current window hash
        curr_hash = 0
        for i, ch in enumerate(reversed(haystack[:n])):
            curr_hash = (curr_hash + (ord(ch) - ord('a')) * pow(base, i, mod)) % mod

        result = []
        base_power = pow(base, n - 1, mod)

        for i in range(len(haystack) - n + 1):
            if needle_hash == curr_hash:
                result.append(i)
            if i + n < len(haystack):
                curr_hash = ((curr_hash - (ord(haystack[i]) - ord('a')) * base_power) * base + (ord(haystack[i + n]) - ord('a'))) % mod

        return result

    def beautifulIndices(self, s: str, a: str, b: str, k: int) -> List[int]:
        if len(a) > len(s) or len(b) > len(s):
            return []

        # Create prefix sum array for b indices
        b_idxs = [0] * len(s)
        for i in self.rabin_karp(s, b):
            b_idxs[i] = 1

        prefix_sum = [0]
        for i in range(len(s)):
            prefix_sum.append(prefix_sum[-1] + b_idxs[i])

        # Check each a index
        result = []
        for i in self.rabin_karp(s, a):
            l = max(i - k, 0)
            r = min(i + k + 1, len(s))
            if prefix_sum[r] - prefix_sum[l] > 0:
                result.append(i)

        return result

