func maximumLength(nums []int) int {
    oddSum := 0
    evenSum := 0
    for _, v := range nums {
      if v%2 == 0 {
        evenSum += 1
      } else {
        oddSum +=1
      }
    }
    previous := nums[0]
    length := 1
    for _, v := range nums {
      if v%2 != previous%2{
        previous = v
        length += 1
      }
    }
    if evenSum >= oddSum && evenSum >= length {
      return evenSum
   } else if oddSum >= evenSum && oddSum >= length {
      return oddSum
   } else {
      return length
   }
}

