def count_partitions(nums)
  total_sum = nums.sum
  (1...nums.size).reduce([0,0]) {|(l_sum, c),i| c+=1 if (total_sum-2*l_sum).even?; [l_sum+nums[i], c] }.last
end

# @param {Integer[]} arr
# @param {Integer[]} brr
# @param {Integer} k
# @return {Integer}
def min_cost(arr, brr, k)
  # there is an order for which cost of making each element equal is minimum, say min_x
  # current_odrer is c_x, if min_x+k < c_x, we use k
  # o1_x, o2_x; 
  [arr.zip(brr).map {|a,b| (a-b).abs}.sum, k + arr.sort.zip(brr.sort).map {|a,b| (a-b).abs}.sum].min
end

# @param {Integer[]} nums
# @param {Integer} k
# @return {Integer}
def sum_of_good_numbers(nums, k)
  nums.each_with_index.select { |num, i| num > (i-k>=0 ? nums[i-k] : 0) && num > nums[i+k].to_i }.map(&:first).sum
end

# @param {String} s
# @return {String}
def find_valid_pair(s)
  n = s.size; chars = s.chars
  freq_h = chars.tally
  (1...n).each { |i|
    return "#{chars[i-1]}#{chars[i]}" if chars[i]!=chars[i-1] && freq_h[chars[i]] == chars[i].to_i && freq_h[chars[i-1]] == chars[i-1].to_i
  }
  ""
end

# @param {Integer} event_time
# @param {Integer[]} start_time
# @param {Integer[]} end_time
# @return {Integer}
Meeting = Struct.new(:start_time, :end_time)
def max_free_time(event_time, start_time, end_time)
  meetings = start_time.zip(end_time).map {|a,b|Meeting.new(a,b) }
  n = meetings.size
  durations = meetings.map {|x| x.end_time-x.start_time }
  free_times = [meetings[0].start_time-0]
  (1...n).each {|i| free_times << meetings[i].start_time-meetings[i-1].end_time }
  free_times << (event_time - meetings[-1].end_time)

  sliding_max = 0;
  pre_max_till_i = [free_times[0]]
  (1..n).each { |i| pre_max_till_i<<[free_times[i], pre_max_till_i[-1]].max; sliding_max=[sliding_max, free_times[i]+free_times[i-1]].max  }

  suf_max_from_i = [free_times[-1]]
  (n-1).downto(0).each { |i| suf_max_from_i.unshift([free_times[i], suf_max_from_i[0]].max) }

  result = sliding_max;

  # calculate for d, if f+d+f is possible
  durations.each_with_index do |duration, i|
    non_neighbor_free_times = [ i>=1 ? pre_max_till_i[i-1] : -Float::INFINITY,  i+2<=n ? suf_max_from_i[i+2] : -Float::INFINITY]
    result = [result, free_times[i]+duration+free_times[i+1]].max  if non_neighbor_free_times.max >= duration
  end

  result
end

# @param {Integer[]} nums
# @return {Integer}
def max_adjacent_distance(nums)
  [(1...nums.size).map {|i| (nums[i]-nums[i-1]).abs }.max, (nums[-1]-nums[0]).abs].max
end

# @param {Integer[][]} grid
# @return {Integer[][]}
def sort_matrix(grid)
  n = grid.size
  diagonal_values = Hash.new {|h,k| h[k]=[]}
  (0...n).each {|i| (0...n).each {|j| diagonal_values[j-i] << grid[i][j] } }
  diagonal_values.each {|k, v| v.sort!; v.reverse! if k>0}
  (0...n).each {|i| (0...n).each {|j| grid[i][j] = diagonal_values[j-i].pop } }
  grid
end

# @param {String} s
# @return {Integer}
def max_difference(s)
  freq_h = s.chars.tally
  odd, even = freq_h.values.partition {|x| x.odd? }  
  odd.max - even.min
end

# @param {Integer[]} groups
# @param {Integer[]} elements
# @return {Integer[]}
def assign_elements(groups, elements)
  max_size = groups.max
  earliest_divisor = {}
  processed_elements = Set.new

  elements.each_with_index do |elem, idx|
    next if processed_elements.include?(elem)

    (1..).each { |i| earliest_divisor[elem*i] ||= idx; break if elem*i > max_size }

    processed_elements << elem
  end

  groups.map { |group| earliest_divisor[group] || -1 }
end

class Solution:
    def maxSum(self, grid: List[List[int]], limits: List[int], k: int) -> int:
        pq = []
        n = len(grid)

        # Add all elements to the heap with their row indices
        for i in range(n):
            for x in grid[i]:
                # Negate x for max heap simulation
                heapq.heappush(pq, (-x, i))

        sum = 0
        num_count = 0

        while pq:
            if num_count == k:
                return sum

            # Get the largest value (negated) and its row index
            neg_num, idx = heapq.heappop(pq)
            num = -neg_num  # Convert back to positive

            if limits[idx] > 0:
                sum += num
                num_count += 1
                limits[idx] -= 1

        return sum

# @param {String} s
# @return {Boolean}
def has_same_digits(s)
  digits = s.chars.map(&:to_i)
  (digits.size-2).times do
    digits = (1...digits.size).map {|i| (digits[i]+digits[i-1])%10 }
  end
  digits[0] == digits[1]
end

public class Solution 
{
    public int MinCost(int n, int[][] edges) 
    {
        var graph = new Dictionary<int, List<(int neighbor, int weight)>>();

        // Initialize graph
        for (int i = 0; i < n; i++) 
        {
            graph[i] = new List<(int, int)>();
        }

        // Build graph
        foreach (var edge in edges) 
        {
            int u = edge[0], v = edge[1], w = edge[2];
            graph[u].Add((v, w));
            graph[v].Add((u, 2 * w));
        }

        // Priority queue using SortedSet for min heap behavior
        var pq = new SortedSet<(int dist, int node)>();
        var visited = new HashSet<int>();

        pq.Add((0, 0));

        while (pq.Count > 0) 
        {
            var (dist, node) = pq.Min;
            pq.Remove(pq.Min);

            if (visited.Contains(node)) 
            {
                continue;
            }

            visited.Add(node);

            if (node == n - 1) 
            {
                return dist;
            }

            foreach (var (neighbor, w) in graph[node]) 
            {
                if (!visited.Contains(neighbor)) 
                {
                    pq.Add((dist + w, neighbor));
                }
            }
        }

        return -1;
    }
}

public class Solution {
    public int MinCost(int[][] grid, int k) {
        int m = grid.Length, n = grid[0].Length;
        int[,] dp = new int[m, n];

        for (int i = 0; i < m; i++)
            for (int j = 0; j < n; j++)
                dp[i, j] = int.MaxValue;

        // Initial DP without jumps
        for (int i = 0; i < m; i++) {
            for (int j = 0; j < n; j++) {
                if (i == 0 && j == 0) { dp[i, j] = 0; continue; }
                int left = j > 0 ? dp[i, j-1] : int.MaxValue;
                int top = i > 0 ? dp[i-1, j] : int.MaxValue;
                dp[i, j] = grid[i][j] + Math.Min(left, top);
            }
        }

        // Process k jumps
        for (int jump = 1; jump <= k; jump++) {
            var cells = new List<(int val, int cost, int i, int j)>();
            for (int i = 0; i < m; i++)
                for (int j = 0; j < n; j++)
                    cells.Add((grid[i][j], dp[i, j], i, j));

            cells.Sort((a, b) => b.val != a.val ? b.val.CompareTo(a.val) : a.cost.CompareTo(b.cost));

            int[,] dpNext = new int[m, n];
            for (int i = 0; i < m; i++)
                for (int j = 0; j < n; j++)
                    dpNext[i, j] = int.MaxValue;

            int currMin = int.MaxValue;
            foreach (var c in cells) {
                currMin = Math.Min(currMin, c.cost);
                dpNext[c.i, c.j] = currMin;
            }

            for (int i = 0; i < m; i++) {
                for (int j = 0; j < n; j++) {
                    if (i == 0 && j == 0) { dpNext[i, j] = 0; continue; }
                    int left = j > 0 ? dpNext[i, j-1] : int.MaxValue;
                    int top = i > 0 ? dpNext[i-1, j] : int.MaxValue;
                    dpNext[i, j] = Math.Min(grid[i][j] + Math.Min(left, top), dpNext[i, j]);
                }
            }

            dp = dpNext;
        }

        return dp[m-1, n-1];
    }
}

# @param {Integer} n
# @param {Integer[][]} edges
# @param {Integer} k
# @return {Integer}
class UnionFind
  def initialize(arr)
    @nodes = {}
    arr.each {|x| @nodes[x]=x}
  end

  def find(i)
    while i!=@nodes[i]
        @nodes[i] = @nodes[@nodes[i]]
        i = @nodes[i]
    end
    return i
  end

  def union(x,y)
    x_parent = find(x)
    y_parent = find(y)

    return false if x_parent == y_parent

    @nodes[x_parent] = y_parent
    return true
  end
end

def min_time(n, edges, k)
  uf = UnionFind.new((0...n).to_a)

  sorted_edges = edges.sort_by {|x| x[-1]}
  comp_count = n
  loop do
    x,y,weight = sorted_edges.pop
    comp_count-=1 if uf.union(x,y)
    return weight if comp_count < k
    return 0 if sorted_edges.size < 1
  end
end

fmax = lambda x, y: x if x > y else y

class Solution:
    def maxProduct(self, nums: List[int]) -> int:
        k = max(nums).bit_length()
        max_mask = 1 << k
        dp = [0] * max_mask
        for x in nums:
            dp[x] = x

        for bit_pos in range(k):
            for mask in range(max_mask):
                if mask>>bit_pos & 1:
                    dp[mask] = fmax(dp[mask], dp[mask ^ (1 << bit_pos)])

        return max(m * dp[(max_mask - 1) ^ m] for m in nums)

# @param {Integer} n
# @return {Integer}
@all_special_nos = []
digits = [1,2,3,4,5,6,7,8,9]

palin_possible=->(dgs) { dgs.count(&:odd?) <= 1 }

build_nums=->(dgs) {
  half, middle = [], ''
  # each d appears d/2 times, with max 1 odd entry
  dgs.each { |d| half+=[d.to_s] * (d/2); middle = d.to_s if d.odd? }

  Set.new(half.permutation.to_a).map(&:join).map {|h_s| h_s + middle + h_s.reverse }.map(&:to_i)
}

choose_digits=->(idx, chosen, length) {
  total = chosen.sum
  return if total > length

  @all_special_nos+= build_nums.call(chosen) if idx == digits.size && total == length && palin_possible.call(chosen)

  return if idx == digits.size

  choose_digits.call(idx+1, chosen+[digits[idx]], length)
  choose_digits.call(idx+1, chosen, length)
}

(1...17).each {|length| choose_digits.call(0, [], length) }

def special_palindrome(n)
  @all_special_nos.sort.bsearch { |x| x> n }
end

# @param {Integer[]} value
# @param {Integer[]} limit
# @return {Integer}
def max_total(value, limit)
  elem_store = Hash.new {|h,k| h[k]=[]}
  limit.zip(value).each { |l, v| elem_store[l] << v }
  elem_store.each.sum { |k,elems| elems.sort.last(k).sum }
end

public class Solution {
    public int XorAfterQueries(int[] nums, int[][] queries) {
      const int MOD = 1000000007;
      var groupedQueries = new Dictionary<(int, int, int), long>();
      foreach (var query in queries) {
          int li = query[0], ri = query[1], ki = query[2], vi = query[3];
          var key = (li, ri, ki);

          if (!groupedQueries.ContainsKey(key)) { groupedQueries[key] = 1;}
          groupedQueries[key] = (groupedQueries[key] * vi) % MOD;
      }

      foreach (var kvp in groupedQueries) {
          var (li, ri, ki) = kvp.Key;
          long multiplier = kvp.Value;

          for (int idx = li; idx <= ri; idx += ki) {
              nums[idx] = (int)((long)nums[idx] * multiplier % MOD);
          }
      }

      int result = 0;
      foreach (int num in nums) {
          result ^= num;
      }

      return result;
    }
}

